
import hashlib 
def object_store(density=3):
    store={}
    hasher = lambda k : k 
    hasher = lambda v :  hashlib.md5(str(v) ).hexdigest()[:density]
    update= lambda k,v:  store.update({hasher(k):v})
    let = lambda k,v : update(hasher(k),v)
    get = lambda k: store[hasher(k)]
    return (let,get,hasher)



def primitive_store():
    store={}
    hasher = lambda k : k 
    update= lambda k,v:  store.update({hasher(k):v})
    let = lambda k,v : update(hasher(k),v)
    get = lambda k: store[hasher(k)]
    return (let,get,hasher)



